//void main(int argc, char *argv[]) {////    get_pd_code_array_from_txt_file();////}#include "plcTopology.h"#include "pd_code_functions.h"int * get_pd_code_array_from_txt_file() {    // variable declarations    FILE *infile, *outfile;    pd_stor_t *flype_circuit;    pd_code_t *next_pd_code;    int i;    long imax;    char line[10]; // 10 is big enough, this will just be used to read empty space and a digit from the txt file    size_t len = 10;    char *endptr;    // end variable declarations    flype_circuit = pd_new_pdstor();    infile = fopen("./pdcodes.txt", "r"); // example file    // iterate through until read is the first line with length not equal to 1    // this will be the number of pd codes contained in the file    while(strlen(fgets(line, len, infile)) == 1) {}    imax = strtol(line, &endptr, 10);    // run through all of the pd codes and add them to flype_circuit    // with the restriction of being isotopically different    printf("Total PD Codes: %ld\n", imax);    for(i=0; i < imax; i++) {        // get the next pd code from the file        next_pd_code = pd_read_KnotTheory(infile);        // add the pd code to the flype_circuit        pd_addto_pdstor(flype_circuit, next_pd_code, DIAGRAM_ISOTOPY);        printf("Done with PD Code %d\n", i + 1);        // because no garbage collection        free(next_pd_code);    }    printf("DONE READING PD CODES\n");    pd_code_t *first_pd_code = pd_stor_firstelt(flype_circuit);    pd_idx_t total_cr, curr_cr, k;    total_cr = sizeof(first_pd_code) - 1; // need to subtract 1 for some reason, sizeof returns 1 + number of crossings    int pd_array[total_cr][4];    for(curr_cr = 0; curr_cr < total_cr; curr_cr++) {        for (k = 0; k < 4; k++) {            pd_array[curr_cr][k] = first_pd_code->cross[curr_cr].edge[k] + 1;        }    }    int j;    for(i = 0; i < total_cr; i++) {        printf("[");        for(j = 0; j < 4; j++) {            printf("%d, ", pd_array[i][j]);        }        printf("]\n");    }    return pd_array;}